(3, 63, 'Wanted Upload', 'wantedupload', '<p>{source}<span style="font-family: courier new, courier, monospace;"><br />&lt;!-- You can place html anywhere within the source tags --&gt;<br /><br /><br />&lt;script language="javascript" type="text/javascript"&gt;<br /> // You can place JavaScript like this<br /> <br />&lt;/script&gt;</span></p>\r\n<p><span style="font-family: courier new, courier, monospace;">&lt;h1&gt;Record Input Successful&lt;/h1&gt;</span></p>\r\n<p><span style="font-family: courier new, courier, monospace;">&lt;?php<br />define(''DB_NAME'', ''final'');<br />define(''DB_USER'', ''root'');<br />define(''DB_PASSWORD'', '''');<br />define(''DB_HOST'', ''localhost'');<br /><br />$link = mysql_connect(DB_HOST, DB_USER, DB_PASSWORD);<br /><br />if(!$link) {<br /> die(''Could not connect: '' . mysql_error());<br />}<br /><br />$db_selected = mysql_select_db(DB_NAME, $link);<br /><br />if (!$db_selected) {<br /> die(''Can\\''t use '' . DB_NAME . '': '' . mysql_error());<br /> <br />}<br />if (isset($_POST[''submit'']))<br />{<br /><br /> $name = $_FILES [''upfile''] [''name''];<br /> $temp = $_FILES [''upfile''] [''tmp_name''];<br /> move_uploaded_file($temp,"Upload/".$name);<br /> $url = "Upload/$name";<br /> <br /> <br /><br /> <br />$sql="INSERT INTO wanted (Image, url, Report_id, Report,Fname, Lname, Alias, Gender, Age, Weight, Height, Address, Reward, Physical, Other, WantedFor, Stat, Status, Complainant)<br />VALUES<br />(''$name'',<br />''$url'',</span></p>\r\n<p><span style="font-family: courier new, courier, monospace;">'''',</span><br /><span style="font-family: courier new, courier, monospace;"><span style="font-family: courier new, courier, monospace;"><span style="font-family: courier new, courier, monospace;">''$_POST[Report]'',</span></span></span></p>\r\n<p><span style="font-family: courier new, courier, monospace;">''$_POST[Fname]'',<br />''$_POST[Lname]'',<br />''$_POST[Alias]'',<br />''$_POST[sex]'',<br />''$_POST[Age]'',<br />''$_POST[Weight]'',<br />''$_POST[Height]'',<br />''$_POST[Address]'',<br />''$_POST[Reward]'',<br />''$_POST[Physical]'',<br />''$_POST[Other]'',</span><br /><span style="font-family: courier new, courier, monospace;"><span style="font-family: courier new, courier, monospace;">''$_POST[WantedFor]'',</span></span></p>\r\n<p><span style="font-family: courier new, courier, monospace;">''$_POST[Stat]'',</span></p>\r\n<p><span style="font-family: courier new, courier, monospace;">''$_POST[Status]'',</span></p>\r\n<p><span style="font-family: courier new, courier, monospace;"><span style="font-family: courier new, courier, monospace;">''$_POST[comp]''</span>)";<br /><br /><br /><br />if (!mysql_query($sql)) {<br /> die(''Error: '' . mysql_error());<br />}<br />}<br />mysql_close($link);<br /></span></p>\r\n<p><span style="font-family: courier new, courier, monospace;">?&gt;</span></p>\r\n<p><span style="font-family: courier new, courier, monospace;"> </span></p>\r\n<p>{/source}</p>', '', 1, 2, '2015-02-02 14:48:14', 388, '', '2015-03-19 20:33:12', 388, 0, '0000-00-00 00:00:00', '2015-02-02 14:48:14', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":false,"urlatext":"","targeta":"","urlb":false,"urlbtext":"","targetb":"","urlc":false,"urlctext":"","targetc":""}', '{"extra-class":""}', 15, 12, '', '', 1, 65, '{"robots":"","author":"","rights":"","xreference":""}', 0, '*', ''),
